import React, { useEffect, useState } from "react";
import Axios from "axios"
import './App.css';
import {BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";

import Home from "./Pages/Home";
import AddCompany from "./Pages/AddCompany";
import Register from "./Pages/Register";
import Login from "./Pages/Login";

// npm start index.js to start  clientside
// node index.js to start server side
//control C to kill 

function App() {



  //register
  const [emailReg, setEmailReg] = useState("");
  const [passwordReg, setPasswordReg] = useState("");
  //login
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  //login status
  const [loginStatus, setLoginStatus] = useState("")
  Axios.defaults.withCredentials = true;


  //register function
  const register = () => {
    Axios.post('http://localhost:3307/register',{
      email: emailReg,
      password: passwordReg
    }).then((response)=> {
      console.log(response);
    });
  }
  //login function
  const login = () => {
    Axios.post('http://localhost:3307/login',{
      email: email,
      password: password
    }).then((response)=> {
      if (response.data.message){
        //console.log(response);
        setLoginStatus(response.data.message);
        } else {
          setLoginStatus(response.data[0].email);

        }
      });
    };




  return (



      <Router>
        <nav>
        <ul>
          <Link to="/home">homeeee</Link>
          <Link to="/register">registerr</Link>
          <Link to="/login">login</Link>
          <Link to="/addcompany">add company</Link>
        </ul>
      </nav>
        <Routes>
          <Route path="/" element={<Home/>} />
          <Route path="/company" element={<AddCompany/>} />
          <Route path="/register" element={<Register/>} />
          <Route path="/login" element={<login/>} />


        </Routes>
    </Router>




  );
} 



export default App;
